import React from 'react'

function Main(props) {
    return (
        <main>
            <div>
                <form 
                onSubmit={props.searchInput} >   
                    <input 
                    type="search" 
                    placeholder="search for an anime" 
                    required 
                    value={props.search} 
                    onChange={find => props.setSearch(find.target.value)}/>
                </form>
            </div>
            <div>
                {props.animelist.map(anime=>(
                    <a>
                        {anime.title}
                    </a>
                ))}
            </div>
        </main>
    )
}

export default Main
