import {React, useState, useEffect} from 'react'
import { Link, useParams } from "react-router-dom";



function AnimeDetails() {
    let {id}=useParams();
    const pathname = window.location.pathname

    const[animeIndex, setAnimeIndex] = useState([]);
    const[animeGenre, setAnimeGenre] = useState([]);
    const [currentUrl, setCurrentUrl] = useState(pathname)

    
    
    
    function SetInfo (data) {
        setAnimeIndex(data);
        setAnimeGenre(data.genres);

        setCurrentUrl(pathname);
        
    }
    
    useEffect(() => {
        GetAnimeDetails()
    },[currentUrl])
    
    const GetAnimeDetails = async (mal) => {
        const data = await fetch(`https://api.jikan.moe/v3/anime/${id}`)
        .then(res => res.json());

        console.log(data);
        SetInfo(data);
       
    };

  

    
    
    
    return (
        <div className="flexDetails">
            <h2>{animeIndex.title}</h2>

            <img src={animeIndex.image_url} alt="" />
            <p>
                rank: #{animeIndex.rank}
            </p>
            <p>
                {animeIndex.score}/10
            </p>

            <h3>sinopsis</h3>
            <p>
                {animeIndex.synopsis}
            </p>

            <iframe width="560" height="315" src={animeIndex.trailer_url} title="YouTube video player" frameborder="0" allow="accelerometer; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>

            <div>
                {animeGenre.map(genre =>(
                    <div>
                        {genre.name}
                    </div>
                ))}
            </div>
         
           
            
       
       

            <Link to="/">
            <button >return</button>
            </Link>
        </div>

    )
}

export default AnimeDetails
