import {useState, useEffect} from 'react'
import { Link, useParams } from "react-router-dom";



function AnimeDetails() {
    let {id}=useParams();
    const[animeIndex, setAnimeIndex] = useState([]);
    
    
    useEffect(() => {
        GetAnimeDetails()
    },[])
    
    const GetAnimeDetails = async (mal) => {
        const data = await fetch(`https://api.jikan.moe/v3/anime/${id}`)
        .then(res => res.json());

        

        console.log(data);
        setAnimeIndex(data);
    };

    
    
    return (
        <div className="flexDetails">
            pagina del anime {id}
            
            <p key={animeIndex.id}>
                {animeIndex.title}
            </p>
            <p>
                {animeIndex.aired.from.slice(0,-16)}
            </p>
            <p>
                {animeIndex.aired.to.slice(0,-18)}
            </p>

            <Link to="/">
            <button >return</button>
            </Link>
        </div>

    )
}

export default AnimeDetails
