import {useState, useEffect} from 'react'
import { Link, useParams } from "react-router-dom";



function AnimeDetails() {
    let {id}=useParams();
    const[animeIndex, setAnimeIndex] = useState([]);
    
    
    useEffect(() => {
        GetAnimeDetails()
    },[])
    
    const GetAnimeDetails = async (mal) => {
        const data = await fetch(`https://api.jikan.moe/v3/anime/${id}`)
        .then(res => res.json());

        

        console.log(data);
        setAnimeIndex(data);
    };

    
    
    return (
        <div className="flexDetails">
            <h2>{animeIndex.title}</h2>

            <img src={animeIndex.image_url} alt="" />
            <p>
                rank: #{animeIndex.rank}
            </p>
            <p>
                {animeIndex.score}/10
            </p>

            <h3>sinopsis</h3>
            <p key={animeIndex.id}>
                {animeIndex.synopsis}
            </p>
            <video src= {animeIndex.trailer_url}></video>
            {animeIndex.genres.map(genre=> (
                <p>
                    {genre.name}
                </p>
            ))}
             {animeIndex.ending_themes.map(theme=> (
                <p>
                    {theme}
                </p>
            ))}
           
       

            <Link to="/">
            <button >return</button>
            </Link>
        </div>

    )
}

export default AnimeDetails
