        GetAnimeDetails()
    },[])
    
    const GetAnimeDetails = async () => {
        const data = await fetch(`https://api.jikan.moe/v3/top/anime/1`)
        .then(res => res.json());
        setAnimeIndex(data);
        console.log(data)
        
