    let {id}=useParams()
    const[animeIndex, setAnimeIndex] = useState([]);
    const GetAnimeDetails = async () => {
        const temp = await fetch(`https://api.jikan.moe/v3/anime/${id}`)
        .then(res => res.json());
 
        setAnimeIndex(temp)
    };
    
    const detail = temp.find((element) => id == element.id);

      useEffect( () => {
        GetAnimeDetails();
      }, []);

    return (
        <div>
            pagina del anime {id}   
            {detail.episodes}
            <Link to="/">
            <button >return</button>
            </Link>
        </div>

    )
}

